package com.example.nike_store;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import com.example.nike_store.adapter.BestSellerRecyclerAdapter;
import com.example.nike_store.adapter.NewReleaseRecyclerAdapter;
import com.example.nike_store.model.BestSeller;
import com.example.nike_store.model.NewRelease;

import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity {

    private RecyclerView recyclerView;
    private RecyclerView bestSellerRecyclerView;
    NewReleaseRecyclerAdapter newReleaseRecyclerAdapter;
    BestSellerRecyclerAdapter bestSellerRecyclerAdapter;
    List<NewRelease> newReleaseList;
    List <BestSeller> bestSellerList;


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);



        recyclerView = findViewById(R.id.recyclerView);
        bestSellerRecyclerView = findViewById(R.id.best_seller_recycler);


        newReleaseList = new ArrayList<>();
        bestSellerList = new ArrayList<>();

        NewRelease product1 = new NewRelease("AIR MAX 200", "data:image/jpeg;base64,","Men Shoes","8000000 ₫");
        NewRelease product2 = new NewRelease("AIR MAX 200", "data:image/jpeg;base64,","Men Shoes","8000000 ₫");
        NewRelease product3 = new NewRelease("AIR MAX 200", "data:image/jpeg;base64,","Men Shoes","8000000 ₫");
        NewRelease product4 = new NewRelease("AIR MAX 200", "data:image/jpeg;base64,","Men Shoes","8000000 ₫");
        NewRelease product5 = new NewRelease("AIR MAX 200", "data:image/jpeg;base64,","Men Shoes","8000000 ₫");

        BestSeller bestSeller1 = new BestSeller("Air Zoom","Sneakers","https://product.hstatic.net/200000563747/product/upload_af3a6e310545489e8b88a9252d53a567.jpg","5689 đ","4.8");
        BestSeller bestSeller2 = new BestSeller("Air Force","Sneakers","https://i8.amplience.net/i/jpl/jd_394104_a?qlt=92&w=750&h=531&v=1&fmt=auto","2456 đ","4.9");
        BestSeller bestSeller3 = new BestSeller("Air Max","Sneakers","https://product.hstatic.net/200000475003/product/19_032c4433535d4a77b434fc5e60dedc2a_master.png","1475 đ","4.4");
        BestSeller bestSeller4 = new BestSeller("Air Jordan","Sneakers","https://essentialhomme.net/wp-content/uploads/2022/07/Air-Jordan-1-Low-New-Emerald-0.jpg","8523 đ","4.5");
        BestSeller bestSeller5 = new BestSeller("Air Air","Sneakers","https://swagger.com.vn/wp-content/uploads/2020/07/Nike-Air-Jordan-4-Retro-Off-White-sail-1.jpg","9999 đ","4.6");

        bestSellerList.add(bestSeller1);
        bestSellerList.add(bestSeller2);
        bestSellerList.add(bestSeller3);
        bestSellerList.add(bestSeller4);
        bestSellerList.add(bestSeller5);


        newReleaseList.add(product1);
        newReleaseList.add(product2);
        newReleaseList.add(product3);
        newReleaseList.add(product4);
        newReleaseList.add(product5);


        newReleaseRecyclerAdapter = new NewReleaseRecyclerAdapter(this, newReleaseList);
        recyclerView.setHasFixedSize(true);
        recyclerView.setLayoutManager(new LinearLayoutManager(this, LinearLayoutManager.HORIZONTAL, false));
        recyclerView.setAdapter(newReleaseRecyclerAdapter);
        newReleaseRecyclerAdapter.notifyDataSetChanged();

        bestSellerRecyclerAdapter = new BestSellerRecyclerAdapter(this, bestSellerList);
        bestSellerRecyclerView.setHasFixedSize(true);
        bestSellerRecyclerView.setLayoutManager(new LinearLayoutManager(this,LinearLayoutManager.VERTICAL, false));
        bestSellerRecyclerView.setAdapter(bestSellerRecyclerAdapter);
        bestSellerRecyclerAdapter.notifyDataSetChanged();

    }
}